import styled from '@emotion/styled';

export const WeatherForm = styled.form`
  width: 30%;
  height: 100%;
  /* max-width: 1024px; */
  min-height: calc(90vh - 50px);
  background-color: #1f82bb;
  opacity: 1;
`;

export const Title = styled.h2`
  font-size: 34px;
  color: #fff;
`;
