import { WeatherCity } from '../components/WeatherCity/WeatherCity';
import { MyLocationWeather } from '../components/MyLocationWeather/MyLocationWeather';
import { useEffect, useState } from 'react';
import { getWeatherData, getLocationWeatherData } from '../Api/apiService';
import { Loader } from '../components/Loader/Loader';

const styles = {
  container: {
    minHeight: 'calc(80vh - 50px)',
    display: 'flex',
    gap: '10px',
    alignItems: 'center',
    justifyContent: 'center',
  },
};

const Home = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [location, setLocation] = useState({
    latitude: null,
    longitude: null,
  });

  useEffect(() => {
    setIsLoading(true);

    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(position => {
        setLocation({
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
        });
      });
    } else {
      alert("Geolocation is not supported by your browser")
    }

    // getLocationWeatherData(50, 36.25);
    if(location.latitude ) {getLocationWeatherData(location.latitude, location.longitude);}
    

    getWeatherData()
      .then()
      .catch()
      .finally(() => setIsLoading(false));
  }, []);

  return (
    <section style={styles.container}>
      <WeatherCity />
      <MyLocationWeather />
      {isLoading && <Loader />}
      <p>{location.latitude}</p>
      <p>{location.longitude}</p>
    </section>
  );
};

export default Home;
