import styled from '@emotion/styled';

export const WeatherForm = styled.div`

  position: fixed;
  top: 0;
  left: 0;
  z-index: 1001;

  width: 70%;
  height: 100%;
  /* min-height: calc(90vh - 50px); */
  /* background-color: #1F618D ; */
  opacity: 1;
`;

export const Title = styled.h1`
  font-size: 34px;
  text-align: center;
  color: #fff;
`;
