import styled from "@emotion/styled";

export const Container = styled.section`

  margin: 20px auto;
  padding: 0px 10px;
  max-width: 1024px;
  min-height: calc(9vh - 50px);

  border-radius: 10px;
  box-shadow: rgba(0, 0, 0, 0.3) 0px 19px 38px, rgba(0, 0, 0, 0.22) 0px 15px 12px;
  backdrop-filter: blur(10px);
`;
