import React, { useEffect, useState } from 'react';
import { ImSearch /*ImPlus*/ } from 'react-icons/im';
import {
  SearchBarForm,
  Container,
  ButtonSearch,
  // ButtonAdd,
  Input,
} from './SearchBar.styled';
import MyLocationCard from '../MyLocationCard/MyLocationCard';
import { apiServiceSearchData } from '../../Api/apiService';

export const SearchBar = ({ onAddCity, weather }) => {
  const [value, setValue] = useState('');
  const [weatherCity, setWeatherCity] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [cities, setCities] = useState([]);

  const addCity = city => {
    if (city.length === 0) {
      alert('Field must be filled');
    } else {
      setCities([...cities, { id: nanoid(), city }]);
    }

    console.log(cities);
  };

  const handleSubmit = e => {
    e.preventDefault();

    onAddCity(value);
    setValue('');
  };

  const handleChange = e => {
    setValue(e.target.value);
  };

  useEffect(() => {
    if (value.trim() === '') {
      return;
    }
    setIsLoading(true);
    // Все запросы должны идти через корневой компонент Home!!!!
    apiServiceSearchData(value)
      .then(weatherCity => setWeatherCity(weatherCity))
      .catch(error => console.error(error))
      .finally(() => setIsLoading(false));
  }, [value]);

  return (
    <Container>
      <SearchBarForm onSubmit={handleSubmit}>
        <ButtonSearch type="submit">
          <ImSearch size={18} />
        </ButtonSearch>
        <Input
          name="inputData"
          value={value}
          onChange={handleChange}
          type="text"
          autoFocus
          autoComplete="off"
          placeholder="Search for a city"
        />
        {/* <ButtonAdd type="onClick">
          <ImPlus size={18} />
        </ButtonAdd> */}
      </SearchBarForm>
      <MyLocationCard weather={weather} />
      <WeatherList cities={cities} weatherCity={weatherCity}/>
    </Container>
  );
};
