import styled from '@emotion/styled';

export const Container = styled.div`
  .block {
    margin-top: 20px;
    width: 90%;
    height: 100px;
    border-style: solid;
    border-width: 2px;
    border-color: rgba(240, 243, 244, 0.8);
    border-radius: 12px;
    background-color: #154360;
    box-shadow: 3px 8px 14px rgba(136, 198, 253, 0.5);
    opacity: 0;
    transition: opacity 1s ease-in-out;
  }

  .block-show {
    opacity: 1;
  }
`;
