import {
  Container,
  WeatherForecast,
  Precipitation,
  Sunrise,
  Wind,
  Visibility,
  Humidity,
} from './WeatherWidgets.styled';

import { WiSunrise, WiSunset } from 'react-icons/wi';

export const WeatherWidgets = ({ weather, forecast }) => {
  const UTCTime = () => {
    let time = new Date(weatherCity.dt*1000);
    return `${time.getUTCHours()}:${time.getUTCMinutes()}`;
   };

  return (
    <Container>
      <WeatherForecast>{forecast}</WeatherForecast>
      <div
        style={{
          display: 'flex',
          flexDirection: 'row',
          flexWrap: 'wrap',
          justifyContent: 'space-between',
          gap: '10px',
          width: '95%',
        }}
      >
        <Precipitation>Precipitation</Precipitation>
        <div
          style={{
            display: 'flex',
            flexDirection: 'row',
            flexWrap: 'wrap',
            alignItems: 'center',
            gap: '10px',
            width: '310px',
          }}
        >
          <Sunrise>
            <WiSunrise />
            Sunrise
            {UTCTime()}
            {/* {new Date(weather.sys.sunrise * 1000).toUTCString()} */}
            <WiSunset />
            Sunset {new Date(weather.sys.sunset * 1000).toUTCString()}
          </Sunrise>
          <Wind>Wind {weather.wind.speed} m/s</Wind>
          <Visibility>Visibility {weather.visibility}</Visibility>
          <Humidity>Humdity {weather.main.humidity}</Humidity>
        </div>
      </div>
    </Container>
  );
};
