import { WeatherCity } from '../components/WeatherCity/WeatherCity';
import { MyLocationWeather } from '../components/MyLocationWeather/MyLocationWeather';
import { useEffect, useState } from 'react';
import { getLocationWeatherData } from '../Api/apiService';
import { Loader } from '../components/Loader/Loader';

const styles = {
  container: {
    minHeight: 'calc(80vh - 50px)',
    display: 'flex',
    gap: '10px',
    alignItems: 'center',
    justifyContent: 'center',
  },
};

const Home = () => {
  const [weather, setWeather] = useState(
    JSON.parse(localStorage.getItem('weather')) ?? {}
  );
  const [isLoading, setIsLoading] = useState(false);
  const [coords, setCoords] = useState(58,);
  console.log(coords);
  console.log(weather);

  const handleSucces = position => {
    const { latitude, longitude } = position.coords;
    setCoords({ latitude, longitude });
  };

  const handleError = error => {
    console.log(error);
  };

  useEffect(() => {
    localStorage.setItem('weather', JSON.stringify(weather));
    setIsLoading(true);

    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(handleSucces, handleError);
    } else {
      alert('Geolocation is not supported by your browser');
    }
    const { latitude, longitude } = coords;
    getLocationWeatherData({ latitude, longitude })
      .then(weather => setWeather(weather))
      .catch(error => console.error(error))
      .finally(() => setIsLoading(false));
  }, []);

  return (
    <section style={styles.container}>
      <WeatherCity weather={weather} />
      <MyLocationWeather weather={weather} />
      {isLoading && <Loader />}
    </section>
  );
};

export default Home;
