import React, { useState, useEffect } from 'react';
import { Logo } from './Logo/Logo';
import { Section, MenuBtn, Clock } from './Header.styled';
import { MenuBurgerImg } from 'icons/IconComponent.jsx';

export const Header = ({ isOpen, setIsOpen }) => {
  // const [isOpen, setIsOpen] = useState(false);
  // console.log('isOpen', isOpen);
  const [currentTime, setCurrentTime] = useState(new Date());

  const currentDate = new Date();
  // const hh = currentDate.getHours();
  // const min = currentDate.getMinutes();
  const day = currentDate.getDate();
  const dayOfWeek = currentDate.getDay();
  console.log(dayOfWeek)
  const year = currentDate.getFullYear();

  const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
  const formattedDate = currentDate.toLocaleDateString(undefined, options);

  const daysOfWeek = ['Вс', 'Пн', 'Вт', 'Ср', 'Чт', 'Пт', 'Сб'];
  const dayName = daysOfWeekdayOfWeek;
  console.log(dayName)

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    return () => {
      clearInterval(intervalId);
    };
  },[])

  return (
    <Section>
      <MenuBtn
        type="button"
        aria-label="burger-menu"
        title="Menu"
        onClick={() => {
          console.log('Header-isOpen', isOpen);
          setIsOpen(!isOpen);
        }}
      >
        <MenuBurgerImg />
      </MenuBtn>
      <Clock>{`${currentTime.toLocaleTimeString()} ${formattedDate}`}</Clock>
      <Logo />
    </Section>
  );
};
