import React, { useEffect, useState } from 'react';
// import { nanoid } from 'nanoid';
import { SearchBar } from '../SearchBar/SearchBar';
import { WeatherForm, Title } from './WeatherCity.styled';
import { WeatherList } from '../WeatherList/WeatherList';
import { apiServiceSearchData } from '../../Api/apiService';
import { Loader } from '../Loader/Loader';

export const WeatherCity = ({ weather }) => {
  const [weatherCity, setWeatherCity] = useState(
    JSON.parse(localStorage.getItem('cities')) ?? []
  );
  const [value, setValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  console.log("",weatherCity)

  const handleSubmit = value => {
    setValue(value);
  };

  const addCity = city => {
    if (city.length === 0) {
      alert('Field must be filled');
    }
  };

  useEffect(() => {
    localStorage.setItem('weatherCity', JSON.stringify(weatherCity));

    if (value.trim() === '') {
      return;
    }

    setIsLoading(true);

    apiServiceSearchData(value)
      .then(weatherCity => setWeatherCity([...weatherCity, { ...weatherCity }]))

      .catch(error => console.error(error))
      .finally(() => setIsLoading(false));
      
  }, [value]);

  return (
    <WeatherForm>
      <Title>Weather</Title>
      <SearchBar
        onSubmit={handleSubmit}
        onAddCity={addCity}
        weather={weather}
      />

      <WeatherList cities={weatherCity} />
      {isLoading && <Loader />}
    </WeatherForm>
  );
};
